import React, { Component } from 'react';
import styled from 'styled-components';

const Container = styled.div`
    display: flex;
    width: 100%;
    height: 30px;
    align-items: center;
    font-size: 10px;
`

class Task extends Component {
    state = { }

    render() { 
        return (
            <Container>{this.props.task.description}</Container>
        );
    }
}
 
export default Task;
