import React, { Component } from 'react';
import styled from 'styled-components';

import Task from './Task';

const Container = styled.div`
    display: flex;
    flex-direction: column;
    width: 100%;
    padding-bottom: 5px;
`

const Label = styled.div`
    display: flex;
    width: calc(100% - 5px);
    height: 30px;
    align-items: center;
    padding-left: 5px;
    font-size: 16px;
    color: #606060;

    &:hover {
        cursor: pointer;
    }
`

const Toggle = styled.div`
    position: relative;
    display: flex;
    width: 25px;
    height: 100%;
    justify-content: center;
    align-items: center;
    transform: ${props => props.groupToggle ? 'translateY(-2px) rotate(0deg)' : 'rotate(-90deg)'};
    transition: 0.2s ease-in-out;

    div {
        position: absolute;
        width: 6px;
        height: 1.5px;
        background-color: #606060;
    }
`

const Rect1 = styled.div`
    transform-origin: right bottom;
    transform: translate(-3px,4px) rotate(45deg);
`

const Rect2 = styled.div`
    transform-origin: left bottom;
    transform: translate(3px,4px) rotate(-45deg);
`

const Text = styled.div`
    display: flex;
    width: auto;
    align-items: center;
`

const Line = styled.div`
    display: flex;
    flex-grow: 1;
    margin-left: 10px;
    margin-right: 20px;
    border-bottom: 1px solid #999999;
`

const Tasks = styled.div`
    display: flex;
    flex-direction: column;
    width: calc(100% - 30px);
    padding: 30px 0;
`

class TaskGroup extends Component {
    state = {  
        groupToggle: false,
        tasks: [
            { id:'sometask1', description:'Wyrzucić śmieci.', date:'10.10.2020', priority:'2' },
            { id:'sometask2', description:'Zrobić pranie.', date:'11.10.2020', priority:'1' },
            { id:'sometask3', description:'Strzelić bujakę po mieście i dostać limo.', date:'12.10.2020', priority:'0' },
        ]

    }

     // -- Events -- 

    onClickLabel = () => {
        this.setState({ groupToggle:!this.state.groupToggle });
    }

    // -- Renders -- 

    renderTasks = () => {
        return this.state.tasks.map(t => {
            return <Task key={t.id} task={t} />
        });
    }

    render() {
        return ( 
            <Container>
                <Label onClick={this.onClickLabel}>
                    <Toggle groupToggle={this.state.groupToggle}>
                        <Rect1 />
                        <Rect2 />
                    </Toggle>
                    <Text>{this.props.name}</Text>
                    <Line />
                </Label>
                <Tasks>
                    {this.state.groupToggle && this.renderTasks()}
                </Tasks>
            </Container>
        );
    }
}
 
export default TaskGroup;
