import React, { Component } from 'react';
import styled from 'styled-components';

import Nav from './Nav';
import Contents from './Contents';
import Details from './Details';

const Container = styled.div`
    display: flex;
    width: 100%;
    height: calc(100vh - 2px);
    margin: 0;
    padding: 0;
`

const Button = styled.div`
    position: fixed;
    left: 10px;
    bottom: 10px;
    width: 50px;
    height: 50px;
    border-radius: 25px;
    background-color: #105510;
    &:hover {
        cursor: pointer;
    }
`

class List extends Component {
    state = {
        navToggle: (window.innerWidth >= 900),
        detailsToggle: (window.innerWidth >= 700)
    }

    onResizeWindow = () => {

        if (window.innerWidth < 700) {
            this.setState({ navToggle:false, detailsToggle:false });
        } else if (window.innerWidth < 900) {
            this.setState({ navToggle:false, detailsToggle:true });
        }
    }

    componentDidMount = () => {
        window.addEventListener('resize', this.onResizeWindow);
    }

    render() { 
        return (
            <Container>
                <Nav navToggle={this.state.navToggle}/>
                <Contents navToggle={this.state.navToggle}/>
                <Details />
                <Button />
            </Container>
        );
    }
}
 
export default List;
