import React, { Component } from 'react';
import styled from 'styled-components';

const Container = styled.div`
    display: flex;
    width: 100vw;
    //height: ${window.innerHeight + 'px'};
    height: 100vh;
    margin: 0;
    padding: 0;
    align-items: center;
    justify-content: center;
`

const Form = styled.form`
    display: flex;
    flex-direction: column;
    width: 30%;
    align-items: center;
    //border: 1px solid black;

    @media (max-width: 900px) {
        width: 80%;
    }

    @media (max-width: 700px) {
        width: 100%;
    }

    input {
        width: 80%;
        border-bottom: 2px solid rgba(0,0,0,0.2);
        font-size: 15px;
        padding: 2px 10px;
        margin-top: 10px;
        transition: 0.2s ease-in-out;
    }

    input:focus {
        border-bottom: 2px solid #114673; 
    }

    input:focus::placeholder {
        color: transparent;
    }

    input:hover {
        cursor: pointer;
        border-bottom: 2px solid #114673; 
    }
`

const Label = styled.div`
    width: 100%;
    text-align: center;
    font-size: 30px;
    font-weight: bold;
`

const InputUsername = styled.input``

const InputPassword = styled.input``

const ButtonSubmit = styled.button`
    width: calc(80% + 20px);
    padding: 8px 0px;;
    border-radius: 5px;
    border: 0;
    margin-top: 30px;
    background-color: #114673;
    color: #ffffff;
    font-size: 14px;
    font-weight: bold;
    transition: 0.2s ease-in-out;

    &:hover {
        cursor: pointer;
        background-color: #215683;
    }
`

class SignIn extends Component {
    state = {
        inputUsernameValue:'',
        inputPasswordValue:''
    }

    onChangeInputUsername = (e) => {
        this.setState({ inputUsernameValue:e.target.value });
    }

    onChangeInputPassword = (e) => {
        this.setState({ inputPasswordValue:e.target.value });
    }

    onSubmitForm = (e) => {
        e.preventDefault();
        console.log('Submitted!');
    }

    componentDidMount = () => {
        window.addEventListener('resize', () => {
            
        });
    }

    render() { 
        return (
            <Container>
                <Form>
                    <Label>Sing in</Label>
                    <InputUsername
                        placeholder='Username'
                        value={this.state.inputUsernameValue}
                        onChange={this.onChangeInputUsername}
                    />
                    <InputPassword
                        placeholder='Password'
                        type='password'
                        value={this.state.inputPasswordValue}
                        onChange={this.onChangeInputPassword}
                    />
                    <ButtonSubmit type='submit' onSubmit={() => alert('test')}>Submit</ButtonSubmit>
                </Form>
            </Container>
           /* <button
                onClick={() => this.props.auth.signIn(() => {
                    this.props.history.push('/');
                })}
            >
                Login
            </button>*/
        );
    }
}
 
export default SignIn;
